import { Button, MenuItem, TextField } from "@material-ui/core";
import '../App.css';
import { useState, useEffect } from 'react';

const Home = () => {
	const [categories, setCategories] = useState([]);
	const [category, setCategory] = useState('');
	const [index, setIndex] = useState('');
	const [difficulty, setDifficulty] = useState('');
	//Fetch categories from Trivia API and store them in a state.

	useEffect(()=>{
    axios.get('https://opentdb.com/api_category.php').then(res => setCategories(res.data.trivia_categories));
  }, [])
	//Define a handler function to switch between categories.

	const handleChange = (e) => {
	    setCategory(e.target.value);
	    let filter = categories.filter(c => c.name === category);
	    setIndex(filter[0]); 
	}

	return (
    <div className="home-wrapper">
	    <TextField 
	      select
	      className="categories"
	      label="Select category"
	      value={category}
	      onChange={handleChange}

		>
	      {categories.map(categ => (<MenuItem value={categ.name} key={categ.id}>{categ.name}</MenuItem>))}
	    </TextField>
	    <br></br>
	    <TextField
            select
            label="Select Difficulty"
            className="categories"
            onChange={(e) => {setDifficulty(e.target.value)}}
            value={difficulty}
        >
            <MenuItem key="Easy" value="easy">
              Easy
            </MenuItem>
            <MenuItem key="Medium" value="medium">
              Medium
            </MenuItem>
            <MenuItem key="Hard" value="hard">
              Hard
            </MenuItem>
        </TextField>
        <br></br>
        <Button type="submit" 
        value="submit" color="secondary" 
        variant="contained" className="categories btn"
        onClick={() => console.log(index)}
        >Start Quiz</Button>

    </div>
  );
}
export default Home;
