	const [options, setOptions] = useState([]);
	let str = questions[i]?.question;

	useEffect(() => {
		console.log(questions);
	}, [questions])

	const Option = () => {
		return (
			options.map(op => <Button>{op}</Button>)
		)
	}

	return (
		<form className="quiz-form">
		<p>{str}</p>
		<Option />
		</form>
		)
}

