import './App.css';
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { useState, useEffect } from 'react';
import axios from 'axios';
import Home from './routes/home';
import Header from './components/header';
import Quiz from './routes/quiz'

function App() {
  const [categories, setCategories] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [score, setScore] = useState(0);
  useEffect(()=>{
    axios.get('https://opentdb.com/api_category.php').then(res => setCategories(res.data.trivia_categories));
  }, []);

  const getQuestions = (category, difficulty) => {
    axios.get(`https://opentdb.com/api.php?amount=10&category=${category}&difficulty=${difficulty}&type=multiple`).then(res => setQuestions(res.data.results));
    console.log(questions);
  }
  return (
    <div>
      <BrowserRouter>
        <Header />
        <Switch className="app">
          <Route path="/" exact>
            <Home categories={categories} getQuestions={getQuestions}/>
          </Route>
          <Route path="/quiz" exact>
            <Quiz 
              questions={questions}
              score={score}
              setScore={setScore}
              setQuestions={setQuestions}/>
          </Route> 
        </Switch>
      </BrowserRouter>
    </div>
  )
}

export default App;
