import { CircularProgress, Button } from "@material-ui/core";
import { useState, useEffect } from 'react';
import '../App.css';
const Question = ({questions}) => {
	const [i, setIndex] = useState(0);
	const [chosen, setChosen] = useState(false);
	let [options, setOptions] = useState([]);
	let str = questions[i]?.question;

	useEffect(() => {
		if(questions.length > 0){
			let ops = questions[i]?.incorrect_answers;
			ops = ops.concat(questions[i]?.correct_answer);
			console.log(ops);
			setOptions(options.concat(ops));
			console.log(ops, options);
		}
	}, [questions])

	const handleCheck = (e) => {
		e.target.value === questions[i]["correct_answer"] ? e.target.style.backgroundColor="green" : console.log("wrong answer");
	}

	const Option = () => {
		return (
			<section className="ops-wrapper">
				{options.map(op => <Button onClick={handleCheck} variant="contained" value={op} className="op-button" key={options.indexOf(op)}>{op}</Button>)}
			</section>
		)
	}

	return (
		<form className="quiz-form">
		<p>{str}</p>
		<Option />
		<Button type='submit'>Next question</Button>
		</form>
		)
}

const Quiz = ({questions}) => {
	return (questions!==[] ? <Question questions={questions}/> : <CircularProgress
          color="inherit"
          size={50}
          thickness={1}
        />);

}
export default Quiz;
